import React, {useEffect, useState} from 'react'
import "../CSS/main.css";
import "../CSS/dialog.css";
function AdminMain() {
    const [userData, setUserData] = useState([])
    const [showDialog, setShowDialog] = useState(false)
    const [selectedUser, setSelectedUser] = useState(null)


    useEffect(()=>{
          fetch(`/api/admin/userlist`)
          .then(response => response.json())
          .then(users => {
            setUserData(users);
            console.log(userData);
          })
          .catch(error => console.error(error));
        
        
      }, [])

      const handleBlockButtonClick = (user) => {
        setShowDialog(true);
        setSelectedUser(user);
    }

      const UserDetails = ({pk_id, name, surname, login}) => {
        return (
          <div className="elder-details">
            <h2>{name} {surname}</h2>
            <h3>{login}</h3>
            <h3>Location</h3>
            <div className="configuration-body">
                <button className='Button-configure'>
                    <img src="/images/configure.jpg"></img>
                </button>
                <button className='Button-configure' onClick={() => handleBlockButtonClick({pk_id, name, surname, login})}>
                    <img src="/images/block.jpg" alt="block"></img>
                </button>
            </div>
          </div>
        );
      };

      const closeDialog = () => {
        setShowDialog(false);
        setSelectedUser(null);
    }

    const confirmBlock = () => {
        blockUser(selectedUser);
        closeDialog();
    }

    const blockUser = async (user) => {
        try {
            const response = await fetch('/api/admin/blockuser', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(user)
            });

            if (!response.ok) {
                throw new Error('Error blocking user');
            }

            const result = await response.json();
            console.log('User blocked:', result);
        } catch (error) {
            console.error('Error blocking user:', error);
        }
    }

    useEffect(() => {
        console.log(userData);
    }, [userData]);

  return (
    <div className="main-background">
      <div className="main-component">
        {userData && userData.length > 0 ? (
          userData.map((person, index) => (
            <UserDetails
              key={index}
              pk_id={person.pk_id}
              name={person.name}
              surname={person.surname}
              login={person.login}
            />
          ))
        ) : (
          <p>No elder data available.</p>
        )}
        {showDialog && (
          <div className="dialog">
            <div className="dialog-content">
              <h3>Are you sure you want to block {selectedUser && `${selectedUser.name} ${selectedUser.surname}`}?</h3>
              <button className="confirm" onClick={confirmBlock}>Confirm</button>
              <button className="decline" onClick={closeDialog}>Cancel</button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default AdminMain
