import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';

function Statistics() {
  const [elderData, setElderData] = useState([]);
  const [elderDetails, setElderDetails] = useState(null);
  const sessionData = sessionStorage.getItem('UserSession');
  const [selectedElder, setSelectedElder] = useState('');

  useEffect(() => {
    const userId = JSON.parse(sessionData);

    const fetchElderList = async () => {
      try {
        const response = await fetch(`/api/caregivers/${userId['user_id']}/elder-list`);
        const elders = await response.json();
        setElderData(elders);
        console.log(elders);
        return elders;
      } catch (error) {
        console.error(error);
      }
    };

    fetchElderList();
  }, []);

  const handleElderChange = (event) => {
    setSelectedElder(event.target.value);
  };

  useEffect(() => {
    if (selectedElder) {
      fetchElderData(selectedElder);
    }
  }, [selectedElder]);

  const fetchElderData = async (elderId) => {
    try {
      console.log(elderId);
      const response = await fetch(`/api/caregivers/${elderId}/statistics`);
      const elderDetails = await response.json();
      setElderDetails(elderDetails);
      console.log(elderDetails);
    } catch (error) {
      console.error(error);
    }
  };

  const generateChartData = () => {
    const labels = elderDetails.emergency.map((e) => e.emergency_date.slice(0, 10));
    const data = {
      labels: labels,
      datasets: [
        {
          label: 'Emergency Count',
          data: elderDetails.emergency.map((e, index) => index + 1),
          fill: false,
          backgroundColor: 'rgba(75,192,192,0.2)',
          borderColor: 'rgba(75,192,192,1)',
          borderWidth: 1,
        },
      ],
    };
    return data;
  };

  return (
    <div>
      <select value={selectedElder} onChange={handleElderChange}>
        <option value="">Select an elder</option>
        {elderData.map((elder, index) => (
          <option key={index} value={elder.id}>
            {elder.name} {elder.sirname}
          </option>
        ))}
      </select>
        {elderDetails && (
        <div>
          <Line data={generateChartData()} />
        </div>
      )}
    </div>
  );
}

export default Statistics;
