import React, { useState } from 'react';
import '../CSS/planhouse.css';

const numRows = 30;
const numCols = 30;

function PlanHouse() {
  const [grid, setGrid] = useState(() => {
    return Array.from({ length: numRows }, () => Array.from({ length: numCols }, () => 0));
  });

  const handleCellClick = (row, col) => {
    console.log("Paspaustas eile " + row)
    console.log("Paspaustas stulpelis " + col)
    const newGrid = JSON.parse(JSON.stringify(grid));
    newGrid[row][col] = newGrid[row][col] ? 0 : 1;
    setGrid(newGrid);
  };

  const handleSubmit = () => {
    console.log(grid);

    // Fetch request to Express.js API
    fetch('http://localhost:5000/api/house-plan-grid', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ grid }),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  };

  return (
    <div>
      <div className="grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell, cellIndex) => (
              <div
                key={cellIndex}
                className={`grid-cell${cell ? ' cell-filled' : ''}`}
                onClick={() => handleCellClick(rowIndex, cellIndex)}
              ></div>
            ))}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default PlanHouse;
