import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import '../CSS/planhouse.css';

const numRows = 30;
const numCols = 30;


function Monitor() {
  const { id, name, sirname, address } = useParams();
  const sessionData = sessionStorage.getItem("UserSession");
  const [gridData, setGridData] = useState({})
  const [location, setLocation] = useState(null);
  const [roomList, setRoomList] = useState(null);
  const [currentLocationColor, setCurrentLocationColor] = useState(null);
  const [grid, setGrid] = useState(() => {
    return Array.from({ length: numRows }, () => Array.from({ length: numCols }, () => 0));
  });

useEffect(() => {
    var userId = JSON.parse(sessionData)

    const fetchHousePlan = async () => {
      try{
        const response = await  fetch(`/api/monitor?name=${encodeURIComponent(name)}&sirname=${encodeURIComponent(sirname)}&address=${encodeURIComponent(address)}&userId=${encodeURIComponent(userId['user_id'])}`);
        const data = await response.json();
        const roomObjects = await data.housePlan.filter(obj => obj.hasOwnProperty('roomId') && obj.hasOwnProperty('roomColor')&& obj.hasOwnProperty('roomName')).map(obj => ({ roomId: obj.roomId, roomColor: obj.roomColor, roomName: obj.roomName }));

        setGridData(data.housePlan);
        setRoomList(roomObjects);
      }
      catch (error){
        console.error(error);
      }
    };

    const fetchLocation = async () => {
        try{
          const response = await fetch(`/api/caregivers/elder/${id}/location`);
          const locationData = await response.json();
          setLocation(locationData);
        }
        catch(error){
          console.error(error);
        }
    };

    fetchHousePlan().then(() => {
      const interval = setInterval(() => {
        fetchLocation();
      }, 5000);

      return () => clearInterval(interval);
    })
    .catch((error) => console.error(error));


}, []);
useEffect(() => {
    if(gridData.length != undefined)
    {
        setGrid(gridData[gridData.length-1].grid);
    }

}, [gridData]);

useEffect(() => {
  console.log(roomList);

}, [roomList]);
useEffect(() => {
  console.log(location);
  if(roomList !== null){
    roomList.forEach(element => {
      if(element.roomName === location.location)
      {
        setCurrentLocationColor(element.roomColor);
      }
    });
  }
}, [location]);

  return (
    <div className=''>
      <div className=''>
      <h1>Monitoring {name} {sirname}</h1>
      <h2>Address: {address}</h2>
      <div className="grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell, cellIndex) => (
              <div
                key={cellIndex}
                className={`grid-cell${cell ? ' cell-filled' : ''}${cell === currentLocationColor ? ' grid-cell-glow' : ''}`}
                style={cell ? { backgroundColor: cell } : {}} 
              ></div>
            ))}
          </div>
        ))}
      </div>
      </div>
    </div>
  );
}

export default Monitor;
