import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import '../CSS/planhouse.css';

const numRows = 30;
const numCols = 30;


function Monitor() {
  const { name, sirname, address } = useParams();
  const sessionData = sessionStorage.getItem("UserSession");
  const [gridData, setGridData] = useState({})
  const [grid, setGrid] = useState(() => {
    return Array.from({ length: numRows }, () => Array.from({ length: numCols }, () => 0));
  });

useEffect(() => {
    var userId = JSON.parse(sessionData)
   

      //console.log(userId["user_id"]);
      fetch(`/api/monitor?name=${encodeURIComponent(name)}&sirname=${encodeURIComponent(sirname)}&address=${encodeURIComponent(address)}&userId=${encodeURIComponent(userId['user_id'])}`)
      .then(response => response.json())
      .then(sentGrid => {
        setGridData(sentGrid.housePlan);

      })
      .catch(error => console.error(error));
}, []);
useEffect(() => {
    if(gridData.length != undefined)
    {
        console.log("yra");
    }

}, [gridData]);

  return (
    <div>
      <h1>Monitoring {name} {sirname}</h1>
      <h2>Address: {address}</h2>
      <div className="grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell, cellIndex) => (
              <div
                key={cellIndex}
                className={`grid-cell${cell ? ' cell-filled' : ''}`}
                style={cell ? { backgroundColor: cell } : {}} // Apply the paintColor as inline style
              ></div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Monitor;
