import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import '../CSS/planhouse.css';

const numRows = 30;
const numCols = 30;


function Monitor() {
  const { id, name, sirname, address } = useParams();
  const sessionData = sessionStorage.getItem("UserSession");
  const [gridData, setGridData] = useState({})
  const [grid, setGrid] = useState(() => {
    return Array.from({ length: numRows }, () => Array.from({ length: numCols }, () => 0));
  });

useEffect(() => {
    var userId = JSON.parse(sessionData)

    const fetchHousePlan = async () => {
      try{
        const response = await  fetch(`/api/monitor?name=${encodeURIComponent(name)}&sirname=${encodeURIComponent(sirname)}&address=${encodeURIComponent(address)}&userId=${encodeURIComponent(userId['user_id'])}`);
        const data = await response.json();
        setGridData(data.housePlan);
      }
      catch (error){
        console.error(error);
      }
    };

    const fetchLocation = async () => {

    };


    fetchHousePlan();
}, []);
useEffect(() => {
    if(gridData.length != undefined)
    {
        setGrid(gridData[gridData.length-1].grid);
    }

}, [gridData]);
useEffect(() => {
    console.debug(grid);

}, [grid]);

  return (
    <div>
      <h1>Monitoring {name} {sirname}</h1>
      <h2>Address: {address}</h2>
      <div className="grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell, cellIndex) => (
              <div
                key={cellIndex}
                className={`grid-cell${cell ? ' cell-filled' : ''}`}
                style={cell ? { backgroundColor: cell } : {}} // Apply the paintColor as inline style
              ></div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Monitor;
