import React, {useEffect, useState} from 'react'
import "../CSS/main.css";
import Header from './Header';
import { Link } from 'react-router-dom';

function Main() {
  const [elderData, setElderData] = useState([{}])
  const sessionData = sessionStorage.getItem("UserSession");
  useEffect(()=>{
    
    const userId = JSON.parse(sessionData);

    console.log(userId['user_id']);

    const fetchElderList = () => {
      fetch(`/api/caregivers/${userId['user_id']}/elder-list`)
        .then((response) => response.json())
        .then((elders) => {
          setElderData(elders);
          console.log(elderData);
        })
        .catch((error) => console.error(error));
    };
    fetchElderList();
    const fetchLocation = () => {
      // Fetch location data for each elder in elderData
      elderData.forEach((elder) => {
        // Replace `your_location_endpoint` with the actual endpoint for fetching location data
        fetch(`/api/caregivers/elder/${elder.id}/location`)
          .then((response) => response.json())
          .then((locationData) => {
            // Do something with the location data for the elder
            console.log(locationData);
          })
          .catch((error) => console.error(error));
      });
    };
    const interval = setInterval(() => {
      fetchLocation();
    }, 1000);

    // Cleanup function to clear the interval
    return () => clearInterval(interval);
    
  }, [])


  
  const handleMonitor = (name, sirname, address) => {
    return `/monitor/${name}/${sirname}/${address}`;
  };
  const handleConfigureClick = (id, name, sirname, address, uuid) => {
    return `/elder-config/${id}/${name}/${sirname}/${address}/${uuid}`;
  };

  const ElderDetails = ({ id, name, sirname, address, uuid, onClickMonitor }) => {
    return (
      <div className="elder-details">
        <h2>{name} {sirname}</h2>
        <Link className='monitor-button' to={onClickMonitor(name, sirname, address)}> Monitor </Link>
        <h3>{address}</h3>
        <h3>Location</h3>
        <div className="configuration-body">
            <Link className='Button-configure' to={handleConfigureClick(id, name, sirname, address, uuid)}>
              <img src="/images/configure.jpg" alt="configure" />
            </Link>
                <button className='Button-configure' >
                    <img src="/images/del.jpg" alt="delete"></img>
                </button>
        </div>
      </div>
    );
  };

  return (
    <div className="main-background">
    <Header />
      <div className='main-component'>
        {elderData && elderData.length > 0 ? (
          elderData.map((person, index) => (
            <ElderDetails
              key={index}
              id={person.id}
              name={person.name}
              sirname={person.sirname}
              address={person.address}
              uuid={person.uuid}
              onClickMonitor={handleMonitor}
            />
          ))
        ) : (
          <p>No elder data available.</p>
        )}
      </div>
    </div>

  )
}

export default Main
