import React, { useEffect, useState } from 'react';
import "../CSS/addperson.css";
function AddPerson() {

    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [pendantUUID, setPendantUUID] = useState('');
    const [location, setLocation] = useState([{}]);
    const [s_location, setSLocation] = useState('');
    const sessionData = sessionStorage.getItem("UserSession");

    useEffect(()=>{
        async function fetchHouseList() {
            try {
              const userId = JSON.parse(sessionData).user_id;
              const response = await fetch(`/api/caregivers/${userId}/house-list`);
              if (response.ok) {
                const location = await response.json();
                
                // do something with the location data
                setLocation(location);
              } else {
                throw new Error(`Failed to fetch house list: ${response.statusText}`);
              }
            } catch (error) {
              console.error(`Error fetching house list: ${error.message}`);
              // handle the error (e.g. show an error message to the user)
            }
          }
        
          fetchHouseList();
      }, [])

      useEffect(() => {
        console.log(s_location);
      }, [s_location]);

    const handleNameChange = (event) => {
      setName(event.target.value);
    };
  
    const handleSurnameChange = (event) => {
      setSurname(event.target.value);
    };
  
    const handlePendantUUIDChange = (event) => {
      setPendantUUID(event.target.value);
    };
  
    const handleLocationChange = (event) => {
        setSLocation(event.target.value);
     
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(name);
        // do something with the form data (e.g. submit it to a server)
      };

  return (
<div className="add-person">
  <h1 className="add-person__title">Add Person</h1>
  <form className="add-person__form" onSubmit={handleSubmit}>
    <div className="add-person__form-group">
      <label className="add-person__label" htmlFor="name">Name:</label>
      <input className="add-person__input" type="text" id="name" value={name} onChange={handleNameChange} />
    </div>
    <div className="add-person__form-group">
      <label className="add-person__label" htmlFor="surname">Surname:</label>
      <input className="add-person__input" type="text" id="surname" value={surname} onChange={handleSurnameChange} />
    </div>
    <div className="add-person__form-group">
      <label className="add-person__label" htmlFor="pendant-uuid">Pendant UUID:</label>
      <input className="add-person__input" type="text" id="pendant-uuid" value={pendantUUID} onChange={handlePendantUUIDChange} />
    </div>
    <div className="add-person__form-group">
      <p className="add-person__location-label">Location:</p>
      <div className="add-person__location-options">
      {location.map((house, index) => (
          <div className="add-person__location-option" key={index}>
          <input className="add-person__location-radio" type="radio" name="houseLocation" value={house.address} onChange={handleLocationChange} />
          <label className="add-person__location-label">{house.address}</label>
          </div>
      ))}
      </div>
    </div>
    <button className="add-person__submit-button" type="submit">Add Person</button>
  </form>
</div>
  )
}

export default AddPerson
