import React, { useEffect, useState } from 'react';
import "../CSS/addperson.css";
function AddPerson() {

    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [pendantUUID, setPendantUUID] = useState('');
    const [location, setLocation] = useState([{}]);
    const sessionData = sessionStorage.getItem("UserSession");

    useEffect(()=>{
    
        var userId = JSON.parse(sessionData)
        fetch(`/api/caregivers/${userId["user_id"]}/house-list`)
        .then(response => response.json())
        .then(addresses => {
            setLocation(addresses);
          console.log(location); // Do something with the retrieved car list
        })
        .catch(error => console.error(error));
        
        
      }, [])

    const handleNameChange = (event) => {
      setName(event.target.value);
    };
  
    const handleSurnameChange = (event) => {
      setSurname(event.target.value);
    };
  
    const handlePendantUUIDChange = (event) => {
      setPendantUUID(event.target.value);
    };
  
    const handleLocationChange = (event) => {
      setLocation(event.target.value);
      console.log(location);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // do something with the form data (e.g. submit it to a server)
      };

      const CareGiverAddresses = ({ Address }) => {
        return (
          <div className="care-locations">
            <input type="radio" value={Address} onChange={handleLocationChange} />
            <br />
          </div>
        );
      };

  return (
    <div>
      <h1>Add Person</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label htmlFor="surname">Surname:</label>
          <input type="text" id="surname" value={surname} onChange={handleSurnameChange} />
        </div>
        <div>
          <label htmlFor="pendant-uuid">Pendant UUID:</label>
          <input type="text" id="pendant-uuid" value={pendantUUID} onChange={handlePendantUUIDChange} />
        </div>
        <div>
          <p>Location:</p>
          <label htmlFor="location1">Location 1</label>
          <input type="radio" id="location1" name="location" value="location1" checked={location === 'location1'} onChange={handleLocationChange} />
          <br />
          <label htmlFor="location2">Location 2</label>
          <input type="radio" id="location2" name="location" value="location2" checked={location === 'location2'} onChange={handleLocationChange} />
          <br />
          <label htmlFor="location3">Location 3</label>
          <input type="radio" id="location3" name="location" value="location3" checked={location === 'location3'} onChange={handleLocationChange} />
          <br />

        </div>
        <button type="submit">Add Person</button>
      </form>
    </div>
  )
}

export default AddPerson
