import React, { useEffect, useState } from 'react';
import '../CSS/statistics.css'
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale , //timescale
  LinearScale, 
  PointElement,
  Tooltip,
  Legend
} from 'chart.js'

import { Bar, Pie } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  BarElement,
  CategoryScale , //timescale
  LinearScale, 
  PointElement,
  Tooltip,
  Legend
);

function Statistics() {
  const [elderData, setElderData] = useState([]);
  const [elderEmergencyDetails, setElderEmergencyDetails] = useState(null);
  const [elderSpentTimeDetails, setElderSpentTimeDetails] = useState(null);
  const sessionData = sessionStorage.getItem('UserSession');
  const [selectedElder, setSelectedElder] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  useEffect(() => {
    const userId = JSON.parse(sessionData);

    const fetchElderList = async () => {
      try {
        const response = await fetch(`/api/caregivers/${userId['user_id']}/elder-list`);
        const elders = await response.json();
        setElderData(elders);
        console.log(elders);
        return elders;
      } catch (error) {
        console.error(error);
      }
    };

    fetchElderList();
  }, []);

  const handleElderChange = (event) => {
    setSelectedElder(event.target.value);
  };

  useEffect(() => {
    if (selectedElder) {
      fetchElderEmergencyData(selectedElder);
      fetchElderTimeSpentData(selectedElder);
    }
  }, [selectedElder]);

  const fetchElderEmergencyData = async (elderId) => {
    try {
      console.log(elderId);
      const response = await fetch(`/api/caregivers/${elderId}/statisticsEmergency`);
      const elderDetails = await response.json();
      setElderEmergencyDetails(elderDetails);
      console.log(elderDetails);
      
    } catch (error) {
      console.error(error);
    }
  };
  const fetchElderTimeSpentData = async (elderId) => {
    try {
      console.log(elderId);
      const response = await fetch(`/api/caregivers/${elderId}/statisticsSpent`);
      const elderDetails = await response.json();
      setElderSpentTimeDetails(elderDetails);
      console.log(elderDetails);
      
    } catch (error) {
      console.error(error);
    }
  };


  const generateChartData = () => {

    const labels = elderEmergencyDetails.map((emergency) => emergency.emergencyDate);
    console.log(labels);
    const data = {
      labels: labels,
      datasets: [
        {
          label: 'Emergency Count',
          data: elderEmergencyDetails.map((emergency) => emergency.emergencyCount),
          backgroundColor: 'rgb(68,114,196)',
          borderColor: 'rgba(75,192,192,1)',
          tension: 0.4
        },
      ],
    };
    return data;
  };

  const msToTime = (ms) => {
    const seconds = Math.floor((ms / 1000) % 60);
    const minutes = Math.floor((ms / (1000 * 60)) % 60);
    const hours = Math.floor((ms / (1000 * 60 * 60)) % 24);
  
    return `${hours}h ${minutes}m ${seconds}s`;
  };

  const generatePieChartData = () => {

    const labels = elderSpentTimeDetails.map((room) => room.room_name);
    const dataValues = elderSpentTimeDetails.map((room) => room.timeSpent);

      const data = {
        labels: labels,
        datasets:[
          {
            data: dataValues ,
            backgroundColor: ['aqua', 'green', 'purple']
          }
        ]

      }
      return data;
  };

  const generatePieChartOptions = () => {
    const options = {
      plugins: {
        tooltip: {
          callbacks: {
            label: function (context) {
              const label = context.label || '';
              const value = context.parsed || 0;
              const formattedTime = msToTime(value);
              return `${label}: ${formattedTime}`;
            },
          },
        },
      },
    };
    return options;
  };

  const generateChartOptions = () => {
    const options = {
     y: {
      beginAtZero: true,
      ticks: {
        stepSize: 1
      }
     }
    };
    return options;
  };

  const RangeSelection = () => {
    <div className="date-range-selector">
  <label htmlFor="start-date">Start Date:</label>
  <input
    type="date"
    id="start-date"
    value={startDate}
    onChange={(e) => setStartDate(e.target.value)}
  />

  <label htmlFor="end-date">End Date:</label>
  <input
    type="date"
    id="end-date"
    value={endDate}
    onChange={(e) => setEndDate(e.target.value)}
  />

  <button onClick={handleDateRangeFilter}>Filter</button>
</div>
  };

  const handleDateRangeFilter = () => {
    if (startDate && endDate) {
      const filteredData = elderEmergencyDetails.filter(
        (emergency) =>
          new Date(emergency.emergencyDate) >= new Date(startDate) &&
          new Date(emergency.emergencyDate) <= new Date(endDate)
      );
      setElderEmergencyDetails(filteredData);
    }
  };

  return (
    <div className='statistics-main-background'>

      <div className='statistics-container'>
      <div className='statistics-upper'>
        {elderEmergencyDetails && (
          <div className='statistics-upper-right'>
            <Bar data={generateChartData()}  options={generateChartOptions()}/>
          </div>
        )}
        {elderSpentTimeDetails && (
          <div className='statistics-upper-left'>
            <Pie data={generatePieChartData()}  options={generatePieChartOptions()}/>
          </div>
        )}
      </div>
      <div className='statistics-lower'>
      <select value={selectedElder} onChange={handleElderChange}>
        <option value="">Select an elder</option>
        {elderData.map((elder, index) => (
          <option key={index} value={elder.id}>
            {elder.name} {elder.sirname}
          </option>
        ))}
      </select>
      </div>
      </div>
    </div>
  );
}

export default Statistics;
